INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (213, 'P07 Commission Locale de Sécurité', 30, 'ARTICLE   :<br/><br/><br/>Les constructeurs installateurs et exploitants sont tenus, chacun en ce qui le concerne, de s''assurer que les installations ou équipements sont établis, maintenus et entretenus, en conformité avec les dispositions de la réglementation. A cet effet, ils font respectivement procéder pendant la construction et périodiquement en cours d''exploitation, aux vérifications nécessaires par des organismes ou personnes agréés dans les conditions fixées par arrêté du Ministère de l''Intérieur et des ministres intéressés. Le contrôle exercé par l''Administration ou par les commissions de sécurité ne les dégage pas des responsabilités qui leur incombent.', 2, 'Non', NULL, 2);
