INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (219, 'P15 Sondages et fouilles', 30, 'ARTICLE               :<br/>Le demandeur devra faire effectuer des sondages à l''emplacement des constructions projetées. Ces sondages seront éventuellement suivis de fouilles si l''intérêt des découvertes le justifie. Sondages et fouilles seront contrôlés par le Service Régional de l''Archéologie avec lequel le pétitionnaire est invité à se mettre en rapport. En cas de non respect de ces prescriptions, un procès verbal sera dressé et demande sera faite auprès du Tribunal Administatif d''annuler le permis de construire, ceci ne préjugeant pas des poursuites pénales qui pourront avoir lieu pour destruction de vestiges archéologiques (article 257-1 du code pénal).', 2, 'Non', NULL, 2);
INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (220, 'P16 Déchets et gravats', 30, 'ARTICLE :<br/><br/><br/>Si le chantier devait générer la production de gravats, il appartiendra à l''entreprise d''éliminer les déchets par ses propres moyens. Les abords du chantier devront être maintenus en parfait état de propreté, le libre écoulement des caniveaux devra être respecté, conformément à l''article 99-7 du règlement sanitaire départemental des B.D.R du 26/3/1979.', 2, 'Non', NULL, 2);
