INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (516, 'R442.18', 85, 'ARTICLE<br/><br/>Toute construction fera l''objet d''une demande de permis de construire qui pourra être accordée, conformément à l''article R442.18 du code de l''urbanisme : <br/><br/>a) Soit à compter de l''achèvement des travaux d''aménagement du lotissement, constaté conformément aux articles R462-1 à R462-10 ;<br/><br/>b) Soit à compter de la délivrance de l''autorisation de procéder à la vente ou à la location des lots avant exécution des travaux, à condition que les équipements desservant le lot soient achever. <br/><br/>Le lotisseur fournit à l''acquéreur un certificat attestant, sous sa responsabilité, l''achévement des équipements mentionnés au b ci-dessus. <br/><br/>Ce certificat est joint à la demande de permis de construire.', 2, 'Non', 3, 2);
