INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (281, 'P07 Commission Locale de Sécurité', 31, 'ARTICLE   :<br/>Les constructeurs installateurs et exploitants sont tenus, chacun en ce qui le concerne, de s''assurer que les installations ou équipements sont établis, maintenus et entretenus, en conformité avec les dispositions de la réglementation. A cet effet, ils font respectivement procéder pendant la construction et périodiquement en cours d''exploitation, aux vérifications nécessaires par des organismes ou personnes agréés dans les conditions fixées par arrêté du Ministère de l''Intérieur et des ministres intéressés. Le contrôle exercé par l''Administration ou par les commissions de sécurité ne les dégage pas des responsabilités qui leur incombent.', 2, 'Non', NULL, 2);
INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (283, 'P09 Accessibilité', 31, 'ARTICLE         :<br/>le pétitionnaire respectera les règles générales de construction prises en application des articles L.111.1 et suivants du Code de la Construction et de l''Habitation, en particulier en ce qui concerne l''accessibilité des bâtiments aux handicapés physiques.', 2, 'Non', NULL, 2);
INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (284, 'P10 Accessibilité 2', 31, 'ARTICLE        :<br/>Les dispositions du décret 94-86 du 26 janvier 1994 et de l''arrêté du 31 mai 1994 relatives aux règles d''accessibilité devront être respectées.<br/><br/>''Les dispositions architecturales et les aménagements des locaux d''habitation, des lieux de travail et des établissements et installations recevant du  notamment les locaux scolaires, universitaires et de formation, doivent être tels que ces locaux et installations soient accessibles aux personnes handicapées.', 2, 'Non', NULL, 2);
INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (285, 'P11 Ouverture établissement', 31, 'ARTICLE          :<br/>Les travaux achevés, le pétitionnaire devra saisir l''autorité compétente (commission Communale de Sécurité, Centre de Secours Principal - Chemin de Bigot à Arles) afin d''obtenir l''arrêté d''ouverture de son établissement, conformément aux articles L.118-8-3CCH et R.111-19-10 et 11CCH)', 2, 'Non', NULL, 2);
INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (286, 'P14 SEA EDF', 31, 'Le pétitionnaire devra se rapprocher de la Société des Eaux d''Arles pour le raccordement aux réseaux d''eau potable et d''assainissement, d''ERDF pour le raccordement électrique et Gaz de France.', 2, 'Non', NULL, 2);
INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (287, 'P15 Sondages et fouilles', 31, 'ARTICLE               :<br/>Le demandeur devra faire effectuer des sondages à l''emplacement des constructions projetées. Ces sondages seront éventuellement suivis de fouilles si l''intérêt des découvertes le justifie. Sondages et fouilles seront contrôlés par le Service Régional de l''Archéologie avec lequel le pétitionnaire est invité à se mettre en rapport. En cas de non respect de ces prescriptions, un procès verbal sera dressé et demande sera faite auprès du Tribunal Administatif d''annuler le permis de construire, ceci ne préjugeant pas des poursuites pénales qui pourront avoir lieu pour destruction de vestiges archéologiques (article 257-1 du code pénal).', 2, 'Non', NULL, 2);
INSERT INTO bible (bible, libelle, evenement, contenu, complement, automatique, dossier_autorisation_type, om_collectivite) VALUES (288, 'P16 Déchets et gravats', 31, 'ARTICLE :<br/>Si le chantier devait générer la production de gravats, il appartiendra à l''entreprise d''éliminer les déchets par ses propres moyens. Les abords du chantier devront être maintenus en parfait état de propreté, le libre écoulement des caniveaux devra être respecté, conformément à l''article 99-7 du règlement sanitaire départemental des B.D.R du 26/3/1979.', 2, 'Non', NULL, 2);
